import "./style.css";

const initialFacts = [
  {
    id: 1,
    text: "React is being developed by Meta (formerly facebook)",
    source: "https://opensource.fb.com/",
    category: "technology",
    votesInteresting: 24,
    votesMindblowing: 9,
    votesFalse: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "Millennial dads spend 3 times as much time with their kids than their fathers spent with them. In 1982, 43% of fathers had never changed a diaper. Today, that number is down to 3%",
    source:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "society",
    votesInteresting: 11,
    votesMindblowing: 2,
    votesFalse: 0,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "Lisbon is the capital of Portugal",
    source: "https://en.wikipedia.org/wiki/Lisbon",
    category: "society",
    votesInteresting: 8,
    votesMindblowing: 3,
    votesFalse: 1,
    createdIn: 2015,
  },
];

const CATEGORIES = [
  { name: "technology", color: "rgb(71, 147, 175)" },
  { name: "science", color: "rgb(156, 175, 170)" },
  { name: "finance", color: "rgb(255, 196, 112)" },
  { name: "society", color: "rgb(221, 87, 70)" },
  { name: "entertainment", color: "rgb(198, 132, 132)" },
  { name: "health", color: "rgb(139, 50, 44)" },
  { name: "history", color: "rgb(225, 170, 116)" },
  { name: "news", color: "rgb(17, 109, 110)" },
];

function App() {
  const appTitle = "Learn With Me";

  return (
    <>
      {/* HEADER COMPONENT */}
      <header className="header">
        <div className="logo">
          <img
            src="robot.png"
            height="80"
            width="80"
            alt="Learn With Me Robot"
          />
          <h1>{appTitle}</h1>
        </div>

        <button className="btn btn-large btn-open">Share a fact</button>
      </header>

      <NewFactForm />

      <main className="main">
        <CategoryFilter />
        <FactList />
      </main>
    </>
  );
}

function NewFactForm() {
  return <form className="fact-form">Fact form</form>;
}

function CategoryFilter() {
  return <aside>Category filter</aside>;
}

function FactList() {
  // TEMPORARY
  const facts = initialFacts;

  return (
    <section>
      <ul className="facts-list">
        {facts.map((fact) => (
          <li class="fact">
            <p>
              React is being developed by Meta (formerly facebook)
              <a
                className="source"
                href="https://opensource.fb.com/"
                target="_blank"
              >
                (Source)
              </a>
            </p>
            <span className="tag" style="background-color: rgb(71, 147, 175)">
              technology
            </span>
            <div className="vote-buttons">
              <button>👍🏻 24</button>
              <button>🤯 9</button>
              <button>⛔️ 4</button>
            </div>
          </li>
        ))}
      </ul>
    </section>
  );
}
export default App;
